class MyTestCase(unittest.TestCase):

    def test_basic_agent_functions(self):

        game = Trajectory.from_exp_psiturk_record(TEST_SHAPE_MAG_FULL_GAME)

        # For each: (1) ensure agent starts off neutral, (2) update beliefs, (3) reset beliefs
        multivariate = MultivariateNormalLearner()
        self.assertEqual(PRIOR_MEAN, multivariate.belief_state.mean["yellow"])
        multivariate.update_beliefs("yellow is good", game)
        self.assertLess(PRIOR_MEAN, multivariate.belief_state.mean["yellow"],
                        msg="Agent {} failed to update belief positively.".format(multivariate))
        multivariate.reset_beliefs()
        self.assertEqual(PRIOR_MEAN, multivariate.belief_state.mean["yellow"])

        # For each: (1) ensure agent starts off neutral, (2) update beliefs, (3) reset beliefs
        nn = StatefulFeedforwardNeuralAgent(fold=9)
        self.assertEqual(PRIOR_MEAN, nn.belief_state["yellow|circle"]["mean"])
        nn.update_beliefs("yellow", game)
        self.assertLess(PRIOR_MEAN, nn.belief_state["yellow|circle"]["mean"],
                        msg="Agent {} failed to update belief positively.".format(nn))
        nn.reset_beliefs()
        self.assertEqual(PRIOR_MEAN, nn.belief_state["yellow|circle"]["mean"])

